import React, { useState, useEffect } from 'react';

// Assuming your JSON data is stored locally as data.json in the src directory
import jsonData from '../data.json';

const PriceChecker = () => {
  const [collections, setCollections] = useState([]);
  const [selectedCollection, setSelectedCollection] = useState(null);
  const [items, setItems] = useState([]);

  // Load the JSON data into state when the component mounts
  useEffect(() => {
    setCollections(jsonData);
  }, []);

  // Handler for when a collection is selected
  const handleCollectionSelect = (event) => {
    const collectionName = event.target.value;
    const collection = collections.find((c) => c.collection === collectionName);
    setSelectedCollection(collectionName);
    setItems(collection ? collection.items : []);
  };

  return (
    <div>
      <select onChange={handleCollectionSelect} value={selectedCollection}>
        <option value="">Select a collection</option>
        {collections.map((collection) => (
          <option key={collection.collection} value={collection.collection}>
            {collection.collection}
          </option>
        ))}
      </select>

      {selectedCollection && (
        <select>
          <option value="">Select an item</option>
          {items.map((item, index) => (
            <option key={index} value={item.skin}>
              {item.weapon} - {item.skin} ({item.quality})
            </option>
          ))}
        </select>
      )}
      
      {/* Other elements like button to fetch prices */}
    </div>
  );
};

export default PriceChecker;
